package com.letcode.problems;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class LongestPalindromeTest {

    LongestPalindrome solution;
    @BeforeEach
    void setUp() {
        solution = new LongestPalindrome();
    }

    @Test
    void longestPalindrome() {
        Assertions.assertEquals("bb",solution.longestPalindrome("bb"));
        Assertions.assertEquals("sknks",solution.longestPalindrome(""));
    }

    @Test
    void longestPalindrome2() {
        Assertions.assertEquals("sknks",solution.longestPalindrome2(""));
    }
    @Test
    void isPalindrome() {
        Assertions.assertTrue(solution.isPalindrome("sknks"));
    }
}